std::vector<dof_id_type> dof_indices;
const auto & dof_map = _sys.dofMap();
dof_map.dof_indices(elem, dof_indices, _var.number());
mooseAssert(dof_indices.size() == dphi.size(), "These must match");
RealTensorValue grad = 0;
for (const auto i : index_range(dof_indices))
  grad += _sys.solution()(dof_indices[i]) * dphi[i][0];
