hfConvection::hfConvection(const InputParameters & parameters)
  : Kernel(parameters),
   //_lamda(getMaterialProperty<Real>("lamda")),
   _miu(getMaterialProperty<Real>("miu")),
   _component(getParam<unsigned int>("component"))
{
  if (_component >= _mesh.dimension())
    paramError("component", "The component cannot be greater than the mesh dimension");
}

Real
hfConvection::computeQpResidual()
{
  // (lamda+miu) * _grad_u[_qp]*_test[_i][_qp](component),散度
  return _miu[_qp]* _grad_u[_qp] * _test[_i][_qp](_component);
}

Real
hfConvection::computeQpJacobian()
{
  // the partial derivative of _grad_u is just _grad_phi[_j](component)
  return _miu[_qp] * _grad_phi[_j][_qp] * _test[_i][_qp](_component);
}

}

