#include "PoreAdvectionSUPG.h"

registerMooseObject("MiracleApp", PoreAdvectionSUPG);

InputParameters
PoreAdvectionSUPG::validParams()
{
  InputParameters params = KernelGrad::validParams();
  params.addRequiredCoupledVar("temperature", "the MOX temperature in K");
  params.addCoupledVar("plutonium_concentration", "the plutonium concentration in range 0 - 1");
  params.addClassDescription("advection-part SUPG of pores");
  return params;
}

PoreAdvectionSUPG::PoreAdvectionSUPG(const InputParameters & parameters)
  : KernelGrad(parameters),
    _temperature(coupledValue("temperature")),
    _temperature_grad(coupledGradient("temperature")),
    _plutonium_concentration(coupledValue("plutonium_concentration"))
{
}

RealVectorValue
PoreAdvectionSUPG::velocity() const
{
  Real universal_gas_constant = 8.31446261815324; //[J/mol/K]
  Real Boltzmann_constant = 1.380649e-23;         //[J/K]
  Real Avogadro_constant = 6.02214179e23;         //[molecules/mol]
  Real corros_section_avg = 3e-10;                //[m^2]
  Real uo2_molecules = 5e17;                      //[molecules/m^3]
  Real He_molecules = 2.46e25;                    //[molecules/m^3]
  Real He_molar = 4.0026020e-3;                   //[kg/mol]
  Real uo2_molar = 270.03e-3;                     //[kg/mol]
  Real diffusion_coeff =
      std::sqrt(8 * universal_gas_constant * _temperature[_qp] / libMesh::pi) /
      (3 * libMesh::pi * std::pow(corros_section_avg, 2) * (He_molecules + uo2_molecules)) *
      std::sqrt((He_molar + uo2_molar) / (He_molar * uo2_molar)); //[m^2/s]

  Real pre_factor_pressure = 0;
  Real vaporization = 0;

  if (_temperature[_qp] < 2000)
  {
    pre_factor_pressure = 0.1 * std::exp(33.785);
    vaporization = 5.98e5;
  }
  else if (_temperature[_qp] < 2200)
  {
    pre_factor_pressure = 0.1 * std::exp(34.107);
    vaporization = 6.17e5;
  }
  else
  {
    pre_factor_pressure = 0.1 * std::exp(35.813);
    vaporization = 6.35e5;
  }
  Real vapour_pressure =
      pre_factor_pressure * std::exp(-vaporization / universal_gas_constant / _temperature[_qp]);

  RealVectorValue velocity_sens =
      5.006e-12 *
      (0.988 + 6.395e-6 * _temperature[_qp] + 3.543e-9 * std::pow(_temperature[_qp], 2) +
       3e-12 * std::pow(_temperature[_qp], 3)) *
      std::pow(_temperature[_qp], -2.5) * vaporization * vapour_pressure * _temperature_grad[_qp];
  return velocity_sens;
}

RealGradient
PoreAdvectionSUPG::precomputeQpResidual()
{
  // Real tau =
  //     _current_elem->hmin() /
  //     (std::pow((velocity() + RealVectorValue(libMesh::TOLERANCE * libMesh::TOLERANCE)).norm(), 2));
  Real tau = _current_elem->hmin() /
             (2 * (velocity() + RealVectorValue(libMesh::TOLERANCE * libMesh::TOLERANCE)).norm());
  return tau * _grad_u[_qp] * velocity() * velocity();
}

RealGradient
PoreAdvectionSUPG::precomputeQpJacobian()
{
  // auto tau = _current_elem->hmin() /
  //            (std::pow((velocity() + RealVectorValue(libMesh::TOLERANCE * libMesh::TOLERANCE)).norm(),2));
  Real tau = _current_elem->hmin() /
             (2 * (velocity() + RealVectorValue(libMesh::TOLERANCE * libMesh::TOLERANCE)).norm());
  return tau * _grad_phi[_j][_qp] * velocity() * velocity();
}

