#include "SidesetBetweenSubdomainUpdater.h"
#include "DisplacedProblem.h"
#include "Assembly.h"

#include "libmesh/parallel_algebra.h"
#include "libmesh/parallel_sync.h"
#include "MooseMeshUtils.h"

registerMooseObject("MooseApp", SidesetBetweenSubdomainUpdater);

InputParameters
SidesetBetweenSubdomainUpdater::validParams()
{
  InputParameters params = DomainUserObject::validParams();
  params.addClassDescription(
      "Creates or updates a sideset strictly between two sets of subdomains, ignoring external "
      "boundaries. If an element has no neighbor, it is NOT assigned to the sideset.");

  // Lists of subdomains on each side
  params.addParam<std::vector<SubdomainName>>("inner_subdomains",
                                              "Subdomain(s) that define the 'inner' side");
  params.addParam<std::vector<SubdomainName>>("outer_subdomains",
                                              "Subdomain(s) that define the 'outer' side");

  // The sideset name/ID to update
  params.addRequiredParam<BoundaryName>("update_sideset_name",
                                        "Name of the sideset to create or update.");

  // We register as a MeshModifier, so the user can control execution order
  params.registerBase("MeshModifier");

  return params;
}

SidesetBetweenSubdomainUpdater::SidesetBetweenSubdomainUpdater(const InputParameters & params)
  : DomainUserObject(params),
    _pid(_communicator.rank()),
    _displaced_problem(_fe_problem.getDisplacedProblem().get()),
    _neighbor_side(_assembly.neighborSide()),
    _boundary_name(getParam<BoundaryName>("update_sideset_name")),
    _boundary_id(_mesh.getBoundaryID(_boundary_name)),
    _boundary_info(_mesh.getMesh().get_boundary_info()),
    _displaced_boundary_info(
        _displaced_problem ? &_displaced_problem->mesh().getMesh().get_boundary_info() : nullptr)
{
  // Read user-specified subdomain names
  const auto & inner_names = getParam<std::vector<SubdomainName>>("inner_subdomains");
  const auto & outer_names = getParam<std::vector<SubdomainName>>("outer_subdomains");

  // Check they exist in the mesh
  for (const auto & name : inner_names)
    if (!MooseMeshUtils::hasSubdomainName(_mesh.getMesh(), name))
      paramError("inner_subdomains", "Block '", name, "' not found in the mesh");

  for (const auto & name : outer_names)
    if (!MooseMeshUtils::hasSubdomainName(_mesh.getMesh(), name))
      paramError("outer_subdomains", "Block '", name, "' not found in the mesh");

  // Convert subdomain names to IDs
  for (auto id : MooseMeshUtils::getSubdomainIDs(_mesh.getMesh(), inner_names))
    _inner_ids.insert(id);

  for (auto id : MooseMeshUtils::getSubdomainIDs(_mesh.getMesh(), outer_names))
    _outer_ids.insert(id);

  // Ensure the sideset name is established in boundary_info
  _boundary_info.sideset_name(_boundary_id) = _boundary_name;
  _boundary_info.nodeset_name(_boundary_id) = _boundary_name;
  if (_displaced_boundary_info)
  {
    _displaced_boundary_info->sideset_name(_boundary_id) = _boundary_name;
    _displaced_boundary_info->nodeset_name(_boundary_id) = _boundary_name;
  }
}

void
SidesetBetweenSubdomainUpdater::initialize()
{
  // Clear the containers that track which sides to add or remove
  _add.clear();
  _remove.clear();
}

void
SidesetBetweenSubdomainUpdater::executeOnExternalSide(const Elem * elem, unsigned int side)
{
  // We ignore external sides for this class: no neighbor => no sideset is assigned
  // If there's a boundary from a previous step, we remove it
  if (_boundary_info.has_boundary_id(elem, side, _boundary_id))
    _remove[_pid].emplace_back(elem->id(), side);
}

void
SidesetBetweenSubdomainUpdater::executeOnInternalSide()
{
  // We have 2 elements: _current_elem and _neighbor_elem
  // We'll check each direction
  processSide(_current_elem, _current_side, _neighbor_elem);
  processSide(_neighbor_elem, _neighbor_side, _current_elem);
}

void
SidesetBetweenSubdomainUpdater::processSide(const Elem * primary_elem,
                                            unsigned short int primary_side,
                                            const Elem * secondary_elem)
{
  // If the primary or secondary is null, skip
  if (!primary_elem || !secondary_elem)
    return;

  const auto id_primary = primary_elem->subdomain_id();
  const auto id_secondary = secondary_elem->subdomain_id();

  // If primary is in "inner" set and secondary is in "outer" set, we WANT a boundary
  if (_inner_ids.count(id_primary) && _outer_ids.count(id_secondary))
  {
    if (!_boundary_info.has_boundary_id(primary_elem, primary_side, _boundary_id))
      _add[primary_elem->processor_id()].emplace_back(primary_elem->id(), primary_side);
  }
  else
  {
    // Otherwise, remove it if it exists
    if (_boundary_info.has_boundary_id(primary_elem, primary_side, _boundary_id))
      _remove[primary_elem->processor_id()].emplace_back(primary_elem->id(), primary_side);
  }
}

void
SidesetBetweenSubdomainUpdater::threadJoin(const UserObject & other)
{
  // Combine add/remove from multi-threading
  const auto & sb = static_cast<const SidesetBetweenSubdomainUpdater &>(other);

  for (const auto & [pid, list] : sb._add)
    _add[pid].insert(_add[pid].end(), list.begin(), list.end());

  for (const auto & [pid, list] : sb._remove)
    _remove[pid].insert(_remove[pid].end(), list.begin(), list.end());
}

void
SidesetBetweenSubdomainUpdater::finalize()
{
  // We now apply the add/remove ops (both local & remote)
  const auto & moose_mesh = _mesh.getMesh();
  const auto * displaced_mesh =
      _displaced_problem ? &_displaced_problem->mesh().getMesh() : nullptr;

  // Define lambdas for the push_parallel_vector_data calls
  auto add_functor = [this, &moose_mesh, displaced_mesh](processor_id_type pid, const SideList & data)
  {
    for (auto & [elem_id, side] : data)
    {
      _boundary_info.add_side(moose_mesh.elem_ptr(elem_id), side, _boundary_id);
      if (_displaced_boundary_info && displaced_mesh)
        _displaced_boundary_info->add_side(displaced_mesh->elem_ptr(elem_id), side, _boundary_id);
    }
  };

  auto remove_functor = [this, &moose_mesh, displaced_mesh](processor_id_type pid, const SideList & data)
  {
    for (auto & [elem_id, side] : data)
    {
      _boundary_info.remove_side(moose_mesh.elem_ptr(elem_id), side, _boundary_id);
      if (_displaced_boundary_info && displaced_mesh)
        _displaced_boundary_info->remove_side(displaced_mesh->elem_ptr(elem_id), side, _boundary_id);
    }
  };

  // Communicate these changes in parallel
  TIMPI::push_parallel_vector_data(_communicator, _remove, remove_functor);
  TIMPI::push_parallel_vector_data(_communicator, _add, add_functor);

  // Sync & update
  auto sync = [&](MooseMesh & mm)
  {
    mm.getMesh().get_boundary_info().parallel_sync_side_ids();
    mm.getMesh().get_boundary_info().parallel_sync_node_ids();
    mm.update();
  };
  sync(_mesh);
  if (_displaced_problem)
    sync(_displaced_problem->mesh());

  // Let MOOSE know the mesh changed (so it can reinit BCs, etc.)
  _fe_problem.meshChanged();
}

